    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(jRadioButton1.isSelected() && !jTextField1.getText().isEmpty())
        {
            cronExpression.setHours("*/" + jTextField1.getText());
            updateCronExpression(cronExpression.getExpression());
            updateCronExpression(cronExpression.getExpression());
            dispose();
        }
        if(jRadioButton2.isSelected())
        {
            cronExpression.setHours(jComboBox1.getSelectedItem().toString());
            cronExpression.setMinutes(jComboBox2.getSelectedItem().toString());
            updateCronExpression(cronExpression.getExpression());
            updateCronExpression(cronExpression.getExpression());
            dispose();
        }
            
    }//GEN-LAST:event_jButton1ActionPerformed

