    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        String tmp = new String("");
        if(jCheckBox1.isSelected())
        {
            tmp = tmp.concat(",MON");
        }
        if(jCheckBox2.isSelected())
        {
            tmp = tmp.concat(",TUE");
        }
        if(jCheckBox3.isSelected())
        {
            tmp = tmp.concat(",WED");
        }
        if(jCheckBox4.isSelected())
        {
            tmp = tmp.concat(",THU");
        }
        if(jCheckBox5.isSelected())
        {
            tmp = tmp.concat(",FRI");
        }
        if(jCheckBox6.isSelected())
        {
            tmp = tmp.concat(",SAT");
        }
        if(jCheckBox7.isSelected())
        {
            tmp = tmp.concat(",SUN");
        }
        if(tmp.startsWith(","))
        {
            tmp = tmp.replaceFirst(",","");
        }
        cronExpression.setDaysOfWeek(tmp);
        cronExpression.setHours(jComboBox5.getSelectedItem().toString());
        cronExpression.setMinutes(jComboBox6.getSelectedItem().toString());
        updateCronExpression(cronExpression.getExpression());
        dispose();
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        
        if(jRadioButton5.isSelected() && !jTextField3.getText().isEmpty() && !jTextField4.getText().isEmpty())
        {
            cronExpression.setDaysOfMonth(jTextField3.getText());
            cronExpression.setMonths("*/" + jTextField4.getText());
        }
        if(jRadioButton6.isSelected() && !jTextField5.getText().isEmpty())
        {
            cronExpression.setMonths("*/" + jTextField5.getText());
            cronExpression.setDaysOfWeek(jComboBox8.getSelectedItem().toString() + "#" + (jComboBox7.getSelectedIndex()+1));
        }
        cronExpression.setHours(jComboBox9.getSelectedItem().toString());
        cronExpression.setMinutes(jComboBox10.getSelectedItem().toString());
        updateCronExpression(cronExpression.getExpression());
        dispose();
    }//GEN-LAST:event_jButton7ActionPerformed

