    public void updateCronExpression(String tmp)
    {
        if("DoctorAppointmentGUI".equals(this.monitorType))
        {
            Main.caregiverGUI.doctorAppointmentGUI.setCronExpression(tmp);
        }
        if("HealthMonitoringGUI".equals(this.monitorType))
        {
            Main.caregiverGUI.healthMonitoringGUI.setCronExpression(tmp);
        }
        if("MedicationsMonitoringGUI".equals(this.monitorType))
        {
            Main.caregiverGUI.medicationsMonitoringGUI.setCronExpression(tmp);
        }
    }
    
    
    public void populateFields(String expression)
    {
        
        cronExpression.setExpression(expression);
        if(!cronExpression.getMonths().equals("*"))
        {
            if(cronExpression.getDaysOfWeek().equals("*"))
            {
                this.jTextField4.setText(cronExpression.getMonths().substring(2));
                this.jTextField3.setText(cronExpression.getDaysOfMonth());
                this.jRadioButton5.setSelected(true);
            }
            else
            {
                this.jComboBox8.setSelectedItem(cronExpression.getDaysOfWeek().substring(0,3));
                this.jComboBox7.setSelectedIndex(Integer.parseInt(cronExpression.getDaysOfWeek().substring(4)));
                this.jTextField5.setText(cronExpression.getMonths().substring(2));
                this.jRadioButton6.setSelected(true);
            }
            this.jComboBox9.setSelectedItem(cronExpression.getHours());
            this.jComboBox10.setSelectedItem(cronExpression.getMinutes());
            jTabbedPane1.setSelectedIndex(3);  
        }
        if(cronExpression.getMonths().equals("*") && !cronExpression.getDaysOfWeek().equals("*"))
        {
            if(cronExpression.getDaysOfWeek().contains("MON"))
            {
                this.jCheckBox1.setSelected(true);
            }
            if(cronExpression.getDaysOfWeek().contains("TUE"))
            {
                this.jCheckBox2.setSelected(true);
            }
            if(cronExpression.getDaysOfWeek().contains("WED"))
            {
                this.jCheckBox3.setSelected(true);
            }
            if(cronExpression.getDaysOfWeek().contains("THU"))
            {
                this.jCheckBox4.setSelected(true);
            }
            if(cronExpression.getDaysOfWeek().contains("FRI"))
            {
                this.jCheckBox5.setSelected(true);
            }
            if(cronExpression.getDaysOfWeek().contains("SAT"))
            {
                this.jCheckBox6.setSelected(true);
            }
            if(cronExpression.getDaysOfWeek().contains("SUN"))
            {
                this.jCheckBox7.setSelected(true);
            }
            this.jComboBox5.setSelectedItem(cronExpression.getHours());
            this.jComboBox6.setSelectedItem(cronExpression.getMinutes());
            jTabbedPane1.setSelectedIndex(2);  
        }
        
        if(cronExpression.getMonths().equals("*") && (cronExpression.getDaysOfWeek().equals("*") || cronExpression.getDaysOfWeek().equals("MON-FRI")))
        {
            if(cronExpression.getDaysOfWeek().equals("*") && !cronExpression.getDaysOfMonth().equals("*"))
            {
                this.jTextField2.setText(cronExpression.getDaysOfMonth().substring(2));
                this.jRadioButton3.setSelected(true);
                this.jComboBox3.setSelectedItem(cronExpression.getHours());
            this.jComboBox4.setSelectedItem(cronExpression.getMinutes());
            }
            if(cronExpression.getDaysOfWeek().equals("MON-FRI"))
            {
                this.jRadioButton4.setSelected(true);
                this.jComboBox3.setSelectedItem(cronExpression.getHours());
            this.jComboBox4.setSelectedItem(cronExpression.getMinutes());
            }

            jTabbedPane1.setSelectedIndex(1);  
        }
        if(cronExpression.getDaysOfMonth().equals("*") && cronExpression.getMonths().equals("*") && cronExpression.getDaysOfWeek().equals("*") )
        {
            if(cronExpression.getHours().contains("/"))
            {
                this.jRadioButton1.setSelected(true);
                this.jTextField1.setText(cronExpression.getHours().substring(2));
            }
            else
            {
                this.jRadioButton2.setSelected(true);
                this.jComboBox1.setSelectedItem(cronExpression.getHours());
                this.jComboBox2.setSelectedItem(cronExpression.getMinutes());
            }
            jTabbedPane1.setSelectedIndex(0);  
        }
        
        
    }

